'use strict';

var Filter = require('broccoli-filter');

function Sextan(inputTree, options) {
  if (!(this instanceof Sextan)) {
    return new Sextan(inputTree, options);
  }

  this.inputTree = inputTree;
  this.options = options;
}

Sextan.prototype = Object.create(Filter.prototype);
Sextan.prototype.constructor = Sextan;
Sextan.prototype.extensions = ['sass', 'scss'];
Sextan.prototype.targetExtension = 'css';
Sextan.prototype.processString = function() {
  return '.chris-eppstein{ color: "red"}\n.is{color: "green"}\n.oss{color: "blue"}\n.dad{color: "#bada55"}';
};

module.exports = {
  name: 'ember-cli-sextan',
  setupPreprocessorRegistry: function(type, registry) {
    registry.add('css', {
      name: 'ember-cli-sextan',
      ext: 'hbs',
      toTree: function(tree) {
        return Sextan(tree);
      }
    });
  }
};