'use strict';

var Filter = require('broccoli-filter');

function Sextant(inputTree, options) {
  if (!(this instanceof Sextant)) {
    return new Sextant(inputTree, options);
  }

  this.inputTree = inputTree;
  this.options = options;
}

Sextant.prototype = Object.create(Filter.prototype);
Sextant.prototype.constructor = Sextant;
Sextant.prototype.extensions = ['sass', 'scss'];
Sextant.prototype.targetExtension = 'css';
Sextant.prototype.processString = function() {
  return '.chris-eppstein{ color: "red"}\n.is{color: "green"}\n.oss{color: "blue"}\n.dad{color: "#bada55"}';
};

module.exports = {
  name: 'ember-cli-sextant',
  setupPreprocessorRegistry: function(type, registry) {
    registry.add('css', {
      name: 'ember-cli-sextant',
      ext: 'hbs',
      toTree: function(tree) {
        return Sextant(tree);
      }
    });
  }
};